@import 'scss/variables.scss';

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 15px;

    @include container {
        font-size: 14px;
    }
}

body {
    font-family: 'Montserrat', sans-serif;
}
.container {
    max-width: 1150px;
    display: block;
    margin: auto;

    @include container {
        padding-left: 25px;
        padding-right: 25px;
    }
}

@import 'scss/button.scss';
@import 'scss/header.scss';
@import 'scss/menu.scss';
@import 'scss/footer.scss';

sources.scss\\variables.scss:
$base-green: #476200;
$soft-black: #1C1C1C;

//--------DEVICE SIZE--------
$small-mobile: 400px;
$tablet-width: 648px;
$desktop-width: 1023px;
$container-width: 1200px;

@mixin small-mobile {
  @media (max-width: $small-mobile) {
    @content;
  }
}

@mixin mobile {
  @media (max-width: #{$tablet-width - 1px}) {
    @content;
  }
}

@mixin tablet {
  @media (max-width: #{$desktop-width}) {
    @content;
  }
}


@mixin desktop {
  @media (min-width: #{$desktop-width + 1px}) {
    @content;
  }
}

@mixin container {
  @media (min-width: #{$desktop-width}) and (max-width: #{$container-width}) {
    @content;
  }
}

sources.scss\\button.scss:
.button {
    font-weight: normal;
    font-size: 1.375rem;
    line-height: 1.875rem;
    color: #FFFFFF;
    padding: 12px 37px;
    border-radius: 0;
    background-color: transparent;
    border: 2px solid #fff;
    position: relative;
    overflow: hidden;
    cursor: pointer;


    &:after,
    &:before {
        content: '';
        display: block;
        background-color: $base-green;
        height: 100%;
        width: 100%;
        top: 0;
        position: absolute;
        transition: ease-in-out .2s;
        z-index: 1;
    }

    &:after {
        transform: translateX(100%);
        right: 0;
    }

    &:before {
        transform: translateX(-100%);
        left: 0;
    }

    span {
        position: relative;
        z-index: 2;
    }

    &:hover {

        &:after,
        &:before {
            transform: translateX(0);
        }
    }

    &_green {
        border: 2px solid $base-green;
        background-color: $base-green;
        transition: all ease-out .2s;

        &::after,
        &::before {
            display: none;
        }

        &:hover {
            background-color: darken($base-green, 10%);
        }
    }
}

