$yellow: #FFED00;
$green: #40C700;

//--------DEVICE SIZE--------
$small-mobile: 400px;
$tablet-width: 648px;
$desktop-width: 1023px;
$container-width: 1200px;

@mixin small-mobile {
  @media (max-width: $small-mobile) {
    @content;
  }
}

@mixin mobile {
  @media (max-width: #{$tablet-width - 1px}) {
    @content;
  }
}

@mixin tablet {
  @media (max-width: #{$desktop-width}) {
    @content;
  }
}


@mixin desktop {
  @media (min-width: #{$desktop-width + 1px}) {
    @content;
  }
}

@mixin container {
  @media (min-width: #{$desktop-width}) and (max-width: #{$container-width}) {
    @content;
  }
}

sources.scss\\button.scss:
.button {
    color: #FFFFFF;
    font-weight: bold;
    font-size: 13px;
    line-height: 25px;
    padding: 7px 22px;
    border-radius: 0;
    background-color: $green;
    position: relative;
    cursor: pointer;
    border-radius: 20px;
    box-shadow: 0px 2px 20px rgba(0, 0, 0, 0.075);

    &:hover {

        &:after,
        &:before {
            transform: translateX(0);
        }
    }

    &_green {
        border: 2px solid $yellow;
        background-color: $yellow;
        transition: all ease-out .2s;

        &::after,
        &::before {
            display: none;
        }

        &:hover {
            background-color: darken($yellow, 10%);
        }
    }
}

sources.scss\\header.scss:
.header {
    padding-top: 10px;
    position: relative;

    .container {
        position: relative;
        z-index: 1;
        display: flex;
        justify-content: space-between;
        align-items: stretch;
    }

    &:before {
        content: '';
        background: $yellow;
        top: 0;
        left: 25px;
        width: 130vw;
        height: 1338px;
        transform: rotate(-7deg) translateY(-50%);
        border-bottom-left-radius: 100px;
        position: absolute;
    }

    &__hot {
        display: flex;
        align-items: flex-end;

        b {
            font-weight: bold;
            padding: 0px 9px 2px;
        }
    }

    &__logo {
        display: block;
        margin-top: 28px;
    }
    
    &__middle {
        flex: auto;
        padding-left: 57px;
        padding-bottom: 10px;
        align-self: flex-end;
        position: relative;
    
        &:before {
            content: '';
            display: block;
            border: 42px solid transparent;
            border-left: 130vw solid #FFF;
            left: 50%;
            bottom: 0;
            transform: translate(-50%, 23%);
            z-index: -1;
            position: absolute;
        }
    }
    
    &__right {
        display: flex;
        flex-direction: column;
        align-items: center;
        padding-top: 5px;
    }

    &__link {
        text-decoration: none;
        color: inherit;
    }

    &__tel {
        font-weight: bold;
        font-size: 20px;
        line-height: 20px;
    }

    &_mail {
        font-weight: 300;
        font-size: 16px;
        line-height: 25px;
    }

    &__callback {
        margin-top: 16px;
    }
}

sources.scss\\menu.scss:
.menu {
    color: #2B2A29;
    display: flex;
    font-weight: normal;
    font-size: 12px;
    line-height: 25px;

    &__item {
        margin-right: 35px;
        list-style: none;

        &:last-child {
            margin-right: 0;
        }

        a {
            text-decoration: none;
            color: inherit;
        }
    }
}
