@import 'scss/variables.scss';

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    font-size: 15px;

    @include container {
        font-size: 14px;
    }
}

body {
    font-family: 'Montserrat', sans-serif;
}
.container {
    max-width: 1150px;
    display: block;
    margin: auto;

    @include container {
        padding-left: 25px;
        padding-right: 25px;
    }
}

@import 'scss/button.scss';
@import 'scss/header.scss';
@import 'scss/menu.scss';
@import 'scss/footer.scss';

sources.scss\\variables.scss:
$base-green: #476200;
$soft-black: #1C1C1C;

//--------DEVICE SIZE--------
$small-mobile: 400px;
$tablet-width: 648px;
$desktop-width: 1023px;
$container-width: 1200px;

@mixin small-mobile {
  @media (max-width: $small-mobile) {
    @content;
  }
}

@mixin mobile {
  @media (max-width: #{$tablet-width - 1px}) {
    @content;
  }
}

@mixin tablet {
  @media (max-width: #{$desktop-width}) {
    @content;
  }
}


@mixin desktop {
  @media (min-width: #{$desktop-width + 1px}) {
    @content;
  }
}

@mixin container {
  @media (min-width: #{$desktop-width}) and (max-width: #{$container-width}) {
    @content;
  }
}

sources.scss\\button.scss:
.button {
    font-weight: normal;
    font-size: 1.375rem;
    line-height: 1.875rem;
    color: #FFFFFF;
    padding: 12px 37px;
    border-radius: 0;
    background-color: transparent;
    border: 2px solid #fff;
    position: relative;
    overflow: hidden;
    cursor: pointer;


    &:after,
    &:before {
        content: '';
        display: block;
        background-color: $base-green;
        height: 100%;
        width: 100%;
        top: 0;
        position: absolute;
        transition: ease-in-out .2s;
        z-index: 1;
    }

    &:after {
        transform: translateX(100%);
        right: 0;
    }

    &:before {
        transform: translateX(-100%);
        left: 0;
    }

    span {
        position: relative;
        z-index: 2;
    }

    &:hover {

        &:after,
        &:before {
            transform: translateX(0);
        }
    }

    &_green {
        border: 2px solid $base-green;
        background-color: $base-green;
        transition: all ease-out .2s;

        &::after,
        &::before {
            display: none;
        }

        &:hover {
            background-color: darken($base-green, 10%);
        }
    }
}

sources.scss\\header.scss:
.header {

    &__nav {
        color: white;
        background-color: $base-green;

        .container {
            display: flex;
            justify-content: space-between;
            align-items: stetch;
        }
    }

    &__right {
        display: flex;
        align-items: center;
    }

    &__soc {
        display: flex;
        justify-content: space-between;
        align-items: center;

        &:after {
            content: '';
            display: block;
            height: 34px;
            width: 1px;
            background-color: #fff;
            margin-left: 25px;
        }
    }

    &__soc-item {
        padding: 0;
        margin: 0;
        margin-right: 14px;
        list-style: none;

        a {
            display: flex;
        }

        &:last-child {
            margin-right: 0;
        }
    }

    &__city {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        height: 100%;
        margin-left: 25px;
        cursor: pointer;
        line-height: 1.1875rem;
        font-size: 0.875rem;
    }

    &__city-top {
        display: flex;
        justify-content: flex-start;
        align-items: center;

        span {
            color: #E8E8E8;
            font-weight: 400;
            margin-left: 3px;
        }
    }

    &__city-arrow {
        transition: ease-in-out .2s;
    }

    &__city-bottom {
        font-weight: bold;
    }

    &__lang {
        height: 100%;
        margin-left: 17px;
        position: relative;
    }

    &__lang-wrap {
        padding: 0px 15px;
        background-color: #fff;
        cursor: pointer;
        display: none;
        align-items: center;
        min-width: 130px;
        height: 100%;
        z-index: 2;
        position: relative;

        &.active {
            display: flex;
        }
    }

    &__lang-select {
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        background-color: #fff;
        position: absolute;
        top: 100%;
        transform: translateY(-100%);
        width: 100%;
        left: 0;
        transition: ease-out .25s;
    }

    &__lang-item {
        display: flex;
        align-items: center;
        padding: 10px 15px;
    }

    &__lang-text {
        font-weight: bold;
        font-size: 0.875rem;
        line-height: 1.1875rem;
        color: $base-green;
        margin-left: 10px;
    }

    &__lang-arrow {
        margin-left: 10px;

        path {
            fill: $base-green;
        }
    }

    &__info {
        padding: 30px 0;

        .container {
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
    }

    &__info-list {
        display: flex;
        align-items: center;
        justify-content: space-between;
        width: 710px;
        padding-right: 41px;
    }

    &__menu {
        .container {
            display: flex;
            justify-content: space-between;
            align-items: stretch;

            &::before {
                content: '';
                display: block;
                border-bottom: 70px solid $base-green;
                border-left: 53px solid transparent;
            }
            
            &::after {
                content: '';
                display: block;
                border-bottom: 70px solid $base-green;
                border-right: 53px solid transparent;
            }

            .menu {
                flex: auto;
            }
        }
    }
}

sources.scss\\menu.scss:
.menu {
    color: white;
    display: flex;
    background-color: $base-green;
    padding: 23px 60px;

    @include container {
        padding: 23px 50px;
    }

    &__item {
        margin-right: 36px;
        list-style: none;

        &:last-child {
            margin-right: 0;
        }

        a {
            text-decoration: none;
            color: inherit;
            font-weight: bold;
            font-size: 1.125rem;
            line-height: 1.5625rem;
            text-transform: uppercase;
        }
    }
}

sources.scss\\footer.scss:
.footer {
    background-color: $soft-black;

    &__media {
        .container {
            display: flex;
            align-items: center;
            justify-content: space-between;
            border-bottom: 1px solid #2C2C2C;
            padding-top: 21px;
            padding-bottom: 21px;
        }
    }

    &__soc {
        display: flex;
    }

    &__soc-item {
        list-style: none;
        margin-right: 3px;

        path {
            transition: ease-in-out .2s;
        }

        &:hover {
            svg>path {
                fill: $base-green;
            }
        }
    }

    &__main {
        .container {
            display: flex;
            align-items: stretch;
        }
    }

    &__main-item {
        width: calc(100%/3);
        border-right: 1px solid #2C2C2C;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        padding-left: 25px;
        padding-bottom: 21px;

        &:last-child {
            border-right: 0;
        }

        .contacts {
            justify-content: center;
            margin-top: 40px;
        }
    }

    &__info {

        .contacts {
            padding-right: 30px;
        }
    }

    &__logo {
        margin-top: 7px;
        display: block;
    }

    &__info-text {
        font-weight: normal;
        font-size: 0.9375rem;
        line-height: 1.25rem;
        color: #FFFFFF;
        max-width: 327px;
        margin-top: 14px;
    }

    &__about {
        margin-top: 17px;
    }

    &__title {
        font-weight: bold;
        font-size: 1.125rem;
        line-height: 1.5625rem;
        color: #FFFFFF;
        margin: 16px 0 26px;
        position: relative;

        &:after {
            content: '';
            width: 42px;
            background-color: #fff;
            height: 2px;
            bottom: -12px;
            left: 0;
            display: block;
            position: absolute;
        }
    }

    &__menu-list {
        max-height: 162px;
        display: flex;
        flex-direction: column;
        flex-wrap: wrap;
        padding: 0;
        margin: 0;
    }

    &__menu-item {
        margin: 0;
        padding: 0;
        list-style: none;

        a {
            font-weight: normal;
            font-size: 0.9375rem;
            line-height: 1.375rem;
            color: #FFFFFF;
            text-decoration: none;

            &:hover {
                .footer__menu-arrow {
                    transform: translateX(3px);
                }
            }
        }
    }

    &__menu-arrow {
        margin-right: 7px;
        transition: ease-in-out .2s;
    }

    // news block
    &__news {
        margin-top: 17px;
    }

    &__news-item {
        display: flex;
        align-items: center;
        padding-bottom: 16px;
        text-decoration: none;
        transition: ease-in-out .2s;

        &:hover {
            transform: translateX(3px);
        }

        &:first-child {
            margin-bottom: 22px;
            border-bottom: 1px solid rgba(255, 255, 255, 0.1);
        }
    }

    &__news-img {
        margin-right: 19px;
        max-height: 64px;
        object-fit: cover;
        object-position: center;
    }

    &__news-date {
        font-weight: normal;
        font-size: 0.9375rem;
        line-height: 1.375rem;
        color: $base-green;
    }

    &__news-desc {
        font-weight: normal;
        font-size: 14px;
        line-height: 19px;
        color: #FFFFFF;
    }

    &__bottom {
        padding: 21px 0;
        border-top: 1px solid rgba(255, 255, 255, 0.1);
        font-weight: normal;
        font-size: 14px;
        line-height: 19px;
        color: #FFFFFF;

        .container {
            display: flex;
            justify-content: space-between;
            align-items: center;
        }
    }

    &__bottom-list {
        display: flex;
        margin: 0;
        padding: 0;

        a {
            text-decoration: none;
            color: inherit;
        }
    }

    &__bottom-list-item {
        padding: 0;
        margin: 0;
        list-style: none;
        margin-right: 26px;

        &:last-child {
            margin-right: 0;
        }
    }
}
